$(function(){$('#form_validation').validate({rules:{'checkbox':{required:!0},'gender':{required:!0}},highlight:function(input){$(input).parents('.form-line').addClass('error')},unhighlight:function(input){$(input).parents('.form-line').removeClass('error')},errorPlacement:function(error,element){$(element).parents('.form-group').append(error)}});$('#form_advanced_validation').validate({rules:{'date':{customdate:!0},'creditcard':{creditcard:!0}},highlight:function(input){$(input).parents('.form-line').addClass('error')},unhighlight:function(input){$(input).parents('.form-line').removeClass('error')},errorPlacement:function(error,element){$(element).parents('.form-group').append(error)}});$.validator.addMethod('customdate',function(value,element){return value.match(/^\d\d\d\d?-\d\d?-\d\d$/)},'Please enter a date in the format YYYY-MM-DD.');$.validator.addMethod('creditcard',function(value,element){return value.match(/^\d\d\d\d?-\d\d\d\d?-\d\d\d\d?-\d\d\d\d$/)},'Please enter a credit card in the format XXXX-XXXX-XXXX-XXXX.')})